import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';

const DashboardPage = () => {
  const [selectedShoe, setSelectedShoe] = useState(null);

  // Get the location object from React Router
  const location = useLocation();

  // Function to extract the selected shoe from the URL parameters
  const extractSelectedShoe = () => {
    const searchParams = new URLSearchParams(location.search);
    const shoe = searchParams.get('shoe');
    setSelectedShoe(shoe);
  };

  useEffect(() => {
    // Call the extractSelectedShoe function when the component mounts
    extractSelectedShoe();
  }, [location]);

  return (
    <div>
      <header>
        <div className="title-container">
          <h1>SHOE COLLECTION</h1>
        </div>
        <nav className="nav-links-container">
          <Link to="/addshoe" className="add-shoe-button">
            Add Shoe
          </Link>
          <Link to="/" id="logout-button">
            Log Out
          </Link>
        </nav>
      </header>
      <main className="dashboard-main">
        {/* Render the selected shoe information */}
        {selectedShoe && (
          <div>
            <h2>Selected Shoe:</h2>
            <p>{selectedShoe}</p>
          </div>
        )}
      </main>
      <footer>
        <p>Copyright Â© 2023 Shoe Collection</p>
      </footer>
    </div>
  );
};

export default DashboardPage;
