import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const AddShoe = ({ onAddShoe }) => {
  const [selectedShoe, setSelectedShoe] = useState('');
  const shoeOptions = ['Shoe A', 'Shoe B', 'Shoe C']; // Replace with your actual shoe options

  const handleSelectChange = (event) => {
    setSelectedShoe(event.target.value);
  };

  const handleAddShoe = () => {
    // Call the onAddShoe function passed as a prop to add the selected shoe to the dashboard
    if (selectedShoe) {
      onAddShoe(selectedShoe);
    }
  };

  return (
      <div>
          <header>
              <div className="title-container">
                  <h1>SHOE COLLECTION</h1>
              </div>
              <nav className="nav-links-container">
                  <Link to="/dashboard" className="add-shoe-button">
                      Home
                  </Link>
                  <Link to="/" id="logout-button">
                      Log Out
                  </Link>
              </nav>
          </header>
          <main className="login-main">
              <div className="form-container">
                  <label htmlFor="shoeSelect">Select a Shoe:</label>
                  <select id="shoeSelect" value={selectedShoe} onChange={handleSelectChange}>
                      <option value="">Select a shoe</option>
                      {shoeOptions.map((shoe, index) => (
                          <option key={index} value={shoe}>
                              {shoe}
                          </option>
                      ))}
                  </select>
                  <button onClick={handleAddShoe} disabled={!selectedShoe}>
                      Add Shoe
                  </button>
              </div>
          </main>
          <footer>
              <p>Copyright Â© 2023 Shoe Collection</p>
          </footer>
      </div>
  );
};

export default AddShoe;
